                "\n",
                "block= [\n",
                "  Dense,\n",
                "  Dropout,\n",
                "  GaussianNoise]\n",
                "\n",
                "args = [(neuron_num,'relu'),(dropout_rate,),(noise_factor,)]\n",
                "\n",
                "model = Sequential()\n",
                "model.add(Input(shape = (X.shape[1],)))\n",
                "\n",
                "\n",
                "repeat_num = 5\n",
                "for i in range(repeat_num):\n",
                "  for layer,arg in zip(block, args):\n",
                "      model.add(layer(*arg))\n",
                "\n",
                "# Output layer (Warstwa wyjściowa)\n",
