                "# użyć funkcji aktywacji sigmoid w ostatniej warstwie.\n",
                "model.add(Dense(1, activation='sigmoid'))\n",
                "\n",
                "model.compile(optimizer=Adam(learning_rate),\n",
                "              loss='binary_crossentropy',\n",
                "              metrics=['accuracy'])\n",
